(function(){
  console.log('in js shinyalert');
  Shiny.addCustomMessageHandler('shiny_alert_handler', function(data){
    var container_el, alert_el, close_button;
    if (data == null) {
      return;
    }
    container_el = $("#" + data.id);
    alert_el = jQuery('<div/>', {
      'class': "alert alert-dismissable alert-" + data.alert_level
    }).prependTo(container_el);
    alert_el.html(data.message);
    close_button = jQuery('<button type="button" class="close" data-dismiss="alert" aria-hidden="true">&times;</button>').prependTo(alert_el);
  });
}).call(this);
